insert  into `kr_article`(`id`,`name`,`desc`,`tag`,`author`,`source`,`cover`,`content`,`sort`,`hide`,`view`,`member_id`,`create_time`,`update_time`,`status`) values (33,'四川省商务厅杨厅长一行莅临科瑞公司指导工作','四川省商务厅杨厅长、计处长、苏处长领导一行莅临科瑞公司指导工作，市商务局温局长、丁书记等领导陪同。','','四川科瑞有限公司','','20170627\\5f9bfc2fb18e86f1f1d410bdb883a7e8.jpg','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2013年6月24日，四川省商务厅杨厅长、计处长、苏处长领导一行莅临科瑞公司指导工作，市商务局温局长、丁书记等领导陪同。</span></p><p style=\"text-align:center\"><img src=\"/upload/20170626/aa7d82410541dd4f1a09f4c262016db2.jpg\" title=\"20130625040809858.jpg\" alt=\"20130625040809858.jpg\"/></p><p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2013年科瑞公司在四川省商务厅、绵阳市商务局的大力支持下，承建了四川省“天府网交会”整体平台，平台将于2013年6月13日~7月6日承载“天府网交会•2013第三届中国川茶网购节”。</span></p><p style=\"text-align:center\"><img src=\"/upload/20170626/5660a5a01b1e532d7299e24af4f4861e.jpg\" title=\"20130625040838348.jpg\" alt=\"20130625040838348.jpg\"/></p><p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;届时，“天府网交会”将采用各类新技术、新形式对川内企业和产品进行展示宣传，成为四川电子商务行业各类活动对外宣传的统一官方平台。它将川内企业整合形成四川省电子商务产业集群，能更好地规范我省电子商务市场，打造统一的四川电子商务产业链，促进我省电子商务市场的全面蓬勃发展.</span></p><p style=\"text-align: center;\"><img src=\"/upload/20170626/b06c152b436f81d46d6017086e3fb742.jpg\" title=\"20130625040855168.jpg\" alt=\"20130625040855168.jpg\"/></p>',0,0,0,0,1498460300,1498615298,1),(23,'中国互联网协会理事长邬贺铨院士一行莅临九洲科瑞调研工作','中国互联网协会理事长邬贺铨院士一行在科瑞公司副总经理粟栗的陪同下，对九洲科瑞公司进行了工作调研。','','四川科瑞有限公司','','20170626\\afd4ac0cfcc315f839b14aad6d69d1bc.jpg','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2016年3月7日下午15时30分，中国互联网协会理事长邬贺铨院士一行在科瑞公司副总经理粟栗的陪同下，对九洲科瑞公司进行了工作调研。在调研过程中，邬贺铨理事长一行领导对科瑞公司“一体两翼一根据地”的业务布局以及“科瑞云”生态系统的建设情况进行了考察，同时对科瑞公司自主研发的“觅来”平台进行了深入的了解。在科瑞公司技术开发人员的引导下，体验了“觅来”APP以及科瑞“商易”系统的操作流程，并在粟栗副总经理的讲解下对科瑞旗下 “觅来”平台的特点、运营情况以及“觅来”PC端3.0版本的研发情况进行了了解。</span></p>',0,0,0,0,1498459292,1498545503,1),(24,'驻绵省政协领导莅临科瑞公司考察调研工作','驻绵省政协领导在科瑞公司副总经理粟栗的陪同下，深入了解了科瑞公司当前业务开展情况。','','四川科瑞有限公司','','20170626\\734ffd7ca5e2967005f7e895309f0f2b.jpg','<p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2016年1月19日上午11时，驻绵省政协领导一行抵达科瑞公司进行工作考察和调研。在科瑞公司副总经理粟栗的陪同下，深入了解了科瑞公司当前业务开展情况。</p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;考察调研期间，公司副总经理粟栗向驻绵省政协领导讲解了公司“一体两翼一根据地”的业务布局、由公司自建自营“觅来”平台和自主研发“商易”系统构成的“科瑞云”生态系统。驻绵省政协领导一行对公司在软件领域以及近年来在电商领域取得的成绩表示祝贺，对公司积极进取、勇于求变、不断创新的企业精神给予了充分的肯定！</p>',0,0,0,0,1498459346,1498545602,1),(25,'九洲科瑞程序员勇夺绵阳市总工会第八届职工技能大赛第二名','九洲科瑞公司员工在绵阳市总工会第八届职工技能大赛取得优异的成绩，展现出了九洲人应有的风采和奋勇创新的科瑞精神。','','四川科瑞有限公司','','20170626\\ec7a18aa02f467079602aefc33f2ff9e.jpg','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2015年12月10日-11日，绵阳市总工会第八届职工技能大赛在中国工程物理研究院培训中心隆重举行。在计算机程序设计员项目的比赛中，九洲科瑞公司员工张尧凭借扎实的理论知识、突出的实践操作能力，激流勇进、突破重围，以优异的表现摘得该项目比赛第二名的佳绩，展现出了九洲人应有的风采和奋勇创新的科瑞精神，为九洲集团和科瑞公司博得此项荣誉。</span></p>',0,0,0,0,1498459415,1498545972,1),(26,'绵阳市委副书记张锦明一行前往科瑞指导工作','绵阳市委副书记张锦明在九洲集团党委书记王国春、科瑞公司总经理徐海滨的陪同下对四川科瑞软件有限责任公司开展指导工作。','','四川科瑞有限公司','','20170626\\646f386defb5ab76a08d5e63282dd19f.jpg','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2015年11月12日上午9时30分，绵阳市委副书记张锦明一行前往四川科瑞软件有限责任公司指导工作，在九洲集团党委书记王国春、科瑞公司总经理徐海滨的陪同下，调研电商企业改革发展情况，对“觅来”、“科瑞云”生态链做了详细的了解。张锦明书记对科瑞在发展过程中与时俱进、开拓创新的精神予以肯定。</span></p>',0,0,0,0,1498459458,1498615214,1),(27,'绵阳市委书记彭宇行莅临科瑞公司视察调研工作','市委书记彭宇行，市委副书记、市长刘超专程前往科瑞公司视察调研工作。','','四川科瑞有限公司','','20170626\\3b1d584d463b24a1906557929fa95223.png','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">彭宇行、刘超在四川科瑞软件有限公司调研电子商务和“互联网+”工作时，听取企业负责人介绍“觅来”电商平台和“科瑞云”平台运营情况&nbsp;（本报记者&nbsp;蒲滔摄）</span><br style=\"font-family: 微软雅黑; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">　　　　本报讯（记者&nbsp;刘鑫）进入互联网时代后，电子商务和“互联网+”成为未来经济发展的重要支柱。7月21日上午，市委书记彭宇行，市委副书记、市长刘超专程前往我市跨境电商产业园及部分电商企业，调研我市电子商务和“互联网+”的发展现状，并主持召开专题会议，研究我市电子商务和“互联网+”工作，强调要把电子商务和“互联网+”作为我市现代服务业发展的“一号工程”抓紧抓好抓出成效，积极推进电子商务和其他产业的深度融合，为全市经济发展增添新动力。</span><br style=\"font-family: 微软雅黑; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">　　　　彭宇行、刘超专程前往高新区尚高跨境电子商务产业园、科创区四川科瑞软件有限公司等处，调研园区建设和部分电商企业发展情况，详细了解我市电子商务和“互联网+”发展现状，鼓励园区和相关企业抢抓当前电商难得发展机遇，迅速抢占业内区域制高点，形成品牌集聚效应，并帮助引进相关人才来绵创新创业，为绵阳电子商务发展作出新的贡献。</span></p>',0,0,0,0,1498459484,1498615227,1),(28,'绵阳市政协副主席罗平一行前往科瑞指导工作','绵阳市政协副主席罗平在九洲总经理孙仲、科瑞总经理徐海滨的陪同下对四川科瑞软件有限责任公司指导工作，','','四川科瑞有限公司','','20170626\\28e6189c2b4aba6081075a40fbe53874.png','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2015年9月11日上午，绵阳市政协副主席罗平一行前往四川科瑞软件有限责任公司指导工作，在九洲总经理孙仲、科瑞总经理徐海滨的陪同下，调研企业发展情况，对“觅来”、“科瑞云”生态链做了详细了解。罗平副主席对科瑞电商运营模式和创新创造力给予了充分肯定。</span></p>',0,0,0,0,1498459795,1498615244,1),(29,'觅来APP正式发布 开启走心定制时代','九洲科瑞旗下购物平台觅商城APP上线发布会在四川绵阳举行。从此开启走心定制时代 。','','四川科瑞有限公司','','20170627\\85762eb67411ce6101e2629c83bb6465.png','<p style=\"text-align:center\"><img src=\"/upload/20170626/648fcd57f95c19a3542d1b2864c4804b.png\" title=\"20151021043258941.png\" alt=\"20151021043258941.png\"/></p><p><span style=\"font-family: Verdana, Geneva, sans-serif; font-size: 14px; line-height: 30px; text-align: center; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;觅来APP正式发布 开启走心定制时代 2015年10月17日，九洲科瑞旗下购物平台觅商城APP上线发布会在四川绵阳举行，作为2015中国(四川)电子商务发展峰会最大的亮点，绵阳市商务局党组书记、局长潘大钦，九洲集团公司董事长杜力平等嘉宾以及各大媒体记者莅临此次盛会，在精彩的T台Show、潮流搭配快闪等节目后一起共同见证了时尚栖息地——觅来APP的全新时刻。</span></p><p style=\"text-align:center\"><img src=\"/upload/20170626/884f10af8cd30ef4413f5157f42b6d16.png\" title=\"20151021043317313.png\" alt=\"20151021043317313.png\"/></p><p><span style=\"font-family: Verdana, Geneva, sans-serif; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;Imelai Your Mode! 是什么，让您如此轻易被打动?作为独具魅力的时尚社交UCD移动端。觅来APP以用户为中心，打造出多维扁平化和极简风格、Mode视觉呈现特色的一站式潮流聚集地，集品牌精选、时尚话题、潮流搭配、社交分享于一体，以时尚话题的情感诉求为主线，为用户提供精致时尚生活;同时用户可直接参与话题商品以及搭配的关注并互动，及时获取最新时尚潮流资讯，与业内知名搭配师、设计师、选品师等时尚达人共同构成独具特色的时尚社交圈，为潮流人群带来了更时尚便捷的购物体验。</span></p><p style=\"text-align:center\"><img src=\"/upload/20170626/1c979b672418c3552b1f983e2dc0d072.png\" title=\"20151021043404134.png\" alt=\"20151021043404134.png\"/></p><p><span style=\"font-family: Verdana, Geneva, sans-serif; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;作为一个只走心选好货的购商城APP，觅来主营护肤美妆、服装配饰、箱包精品、高端腕表、3C电器等国际知名品牌的时尚正品，同时与1000多家国内外知名品牌商建立合作关系，提供100%正品保障、全场免邮、货到付款以及30天无条件退换货等优质服务。</span></p>',0,0,0,0,1498459960,1498615258,1),(30,'绵阳市委书记罗强莅临科瑞公司视察指导工作','罗强书记对科瑞公司10多年来专注软件与电子商务发展并取得好的成绩表示祝贺，希望企业抢抓当前电商难得的发展机遇，迅速抢占业内区域制高点，形成品牌积聚效应，并帮助引进相关人才来科技城创新创业，为绵阳电子商务发展作出新贡献。','','四川科瑞有限公司','','20170627\\c28e683ea81a4a3e54a7cfef03d7cd17.jpg','<p style=\"text-align:center;padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><strong>绵阳市委书记罗强莅临科瑞公司视察指导工作</strong></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: 微软雅黑; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2014年12月17日，绵阳市委书记罗强莅临四川科瑞软件有限责任公司视察指导工作。罗强书记对科瑞公司10多年来专注软件与电子商务发展并取得好的成绩表示祝贺，希望企业抢抓当前电商难得的发展机遇，迅速抢占业内区域制高点，形成品牌积聚效应，并帮助引进相关人才来科技城创新创业，为绵阳电子商务发展作出新贡献。罗书记对科瑞公司寄予厚望并表示要充分利用科技城人才、技术优势，迅速做大做强电子商务产业，助推我市产业转型升级。罗强书记强调，当前我市电子商务发展势头良好，但业内竞争激烈，决不能掉以轻心。要在现有发展基础上，高水平编制我市电商发展规划，充分发挥科技城人才、技术优势，加大政策扶持和资金投入力度，鼓励绵阳本土实体企业推行线上线下结合销售，积极支持企业开展跨境电商，支持鼓励电子商务快速发展。要加大招商引资力度，促进在谈重点电商项目尽快落地，吸引更多电商人才来绵创新创业，支持鼓励绵阳高校和职业院校培育电商人才、发展电商企业。同时，要积极学习借鉴先进经验，推动电商进农村、进社区等工作，迅速做大做强我市电商产业。</p><p style=\"text-align:center\"><img src=\"/upload/20170627/7430c04307be54a3c87f35c3e035b5a1.jpg\" title=\"20141222031756412.jpg\" alt=\"20141222031756412.jpg\"/></p><p style=\"text-align: center;\"><img src=\"/upload/20170627/643131b181843ca979ac4469b3a5c0cc.jpg\" title=\"20141222031905357.jpg\" alt=\"20141222031905357.jpg\"/></p>',0,0,0,0,1498460048,1498615274,1),(31,'热烈祝贺科瑞公司取得《中国合格评定国家认可委员会实验室认可证书》','科瑞公司顺利通过中国合格评定国家认可委员会（CNAS）和中国国防科技工业实验室认可委员会（DILAC） 对公司进行的现场评审。','','四川科瑞有限公司','','20170626\\3e096b5125bc2d08e6cea37de12698f1.jpg','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2014年8月，科瑞公司顺利通过中国合格评定国家认可委员会（CNAS）和中国国防科技工业实验室认可委员会（DILAC） 对公司进行的现场评审，并于2014年11月正式取得《中国合格评定国家认可委员会 实验室认可证书》。</span></p>',0,0,0,0,1498460083,1498615287,1),(32,'热烈祝贺科瑞公司团支部获”绵阳市五四红旗团支部”荣誉称号','科瑞公司团支部是一支朝气蓬勃的青年队伍，他们技术扎实、业务能力强，富有开拓精神，是公司发展的重要力量。','','四川科瑞有限公司','','20170626\\d4bf678e6a34d88781bba06abfcc6769.jpg','<p><span style=\"font-family: 微软雅黑; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2014年5月,科瑞公司团支部被共青团绵阳市委授予”绵阳市五四红旗团支部”荣誉称号。科瑞公司团支部是一支朝气蓬勃的青年队伍，他们技术扎实、业务能力强，富有开拓精神，是公司发展的重要力量。此次荣誉的获得，是对科瑞公司团支部的肯定与鼓舞，激励大家奋勇向前，取得更大的成绩。</span></p>',0,0,0,0,1498460134,1498615392,1),(34,'2013年四川省电子商务应用培训会在绵阳召开','四川科瑞软件有限责任公司等协办的“2013年四川省电子商务应用（绵阳站）培训会”5月21日隆重举办。','','四川科瑞有限公司','','20170627\\3a692b93172ce445e1ededfee800d542.jpg','<p>&nbsp;&nbsp;&nbsp;&nbsp;为了更好地帮助绵阳市生产和流通企业把握电子商务发展趋势，了解电子商务发展现状，提升电子商务应用能力，提高商务主管部门的电子商务工作水平，由省商务厅、绵阳市商务局主办，省电子商务协会承办，四川科瑞软件有限责任公司等协办的“2013年四川省电子商务应用（绵阳站）培训会”5月21日隆重举办。绵阳市各区市县（园区）商务主管部门、256家生产企业、商贸流通企业及重点电商企业代表共310人参加培训。</p><p style=\"text-align:center\"><img src=\"/upload/20170626/50362d66af658989f85b863231ee75c5.jpg\" title=\"20130524025239413.jpg\" alt=\"20130524025239413.jpg\"/></p><p><span style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;会上，市商务局党组成员、机关党委书记丁明勇同志到会致辞并就我市电商行业发展情况作了介绍。省商务厅罗汝奎同志结合实际案例，讲解了电子商务基本知识、发展现状和传统企业在发展电子商务的战略布局等知识。电子商务业内专家就企业网站建设、SEO、网络推广、电子支付、三方平台、现代物流等方面为企业进行了讲解，参训企业与讲座专家开展了交流互动。</span></p><p style=\"text-align:center\"><span style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\"><img src=\"/upload/20170626/080950de1d85e2296fe5ef8462bedded.jpg\" title=\"20130524025315187.jpg\" alt=\"20130524025315187.jpg\"/></span></p><p><span style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;我公司常务副总经理、绵阳电子商务协会秘书长朱晓上同志在会上分享了我公司电子商务工作开展现状及取得的成果，介绍了我公司蜀家同城（http://shop.yummall.com/）、蜀家商城（http://www.816go.com/）等平台的建设情况，同时对各级政府给予的指导和帮助、对企业给予绵阳电子商务协会工作的支持表示感谢。</span><br style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\">　　通过培训，加深了我市传统企业对电商应用的了解和认识，让企业更深刻的认识到电子商务的特点和应用模式以及发展方向，了解到在现代信息时代企业经济管理模式，有效提升我市电子商务应用水平，助推绵阳电子商务更好的发展。</span></span></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑; font-size: 14px; line-height: 30px; widows: 1; background-color: rgb(255, 255, 255);\"><img src=\"/upload/20170626/05f222a3583f08af02e7e4cfd209510d.jpg\" title=\"20130524025726487.jpg\" alt=\"20130524025726487.jpg\"/></span></p>',0,0,0,0,1498460456,1498615362,1),(35,'绵阳市人民政府李亚莲副市长一行莅临科瑞公司视察','绵阳市人民政府李亚莲副市长莅临科瑞公司视察指导工作，市商务局侍局长等领导陪同。','','四川科瑞有限公司','','20170627\\54740d9c076db83613c931df52e11d94.jpg','<p><span style=\"color: rgb(51, 51, 51); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2013年3月13日，绵阳市人民政府李亚莲副市长莅临科瑞公司视察指导工作，市商务局侍局长等领导陪同。</span><br style=\"color: rgb(51, 51, 51); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; padding: 0px; margin: 0px; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(51, 51, 51); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);\">　　春光明媚，亚莲副市长一行莅临科瑞公司，公司全体员工热情欢迎亚莲副市长一行领导。</span></p><p style=\"text-align:center\"><img src=\"/upload/20170626/ea2af416ed39669e22f66c076f948390.jpg\" title=\"20130315045549182.jpg\" alt=\"20130315045549182.jpg\"/></p><p><span style=\"color: rgb(51, 51, 51); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;亚莲副市长参观科瑞公司已取得的资质、荣誉。</span></p><p style=\"text-align:center\"><img src=\"/upload/20170626/285b8fbe501326b029dbf3cf03a4604e.jpg\" title=\"20130315045549561.jpg\" alt=\"20130315045549561.jpg\"/></p><p><span style=\"color: rgb(51, 51, 51); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;亚莲副市长亲切问候科瑞员工，科瑞同仁们对亚莲副市长的到来热烈欢迎。</span></p><p style=\"text-align:center\"><span style=\"color: rgb(51, 51, 51); font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);\"><img src=\"/upload/20170626/fe3a17a8903cb7c61cac0c30bdd1c553.jpg\" title=\"20130315045549404.jpg\" alt=\"20130315045549404.jpg\"/></span></p><p><span style=\"color: rgb(51, 51, 51); font-family: tahom