USE `renpeng`;

/*Table structure for table `rp_article` */

DROP TABLE IF EXISTS `rp_article`;

CREATE TABLE `rp_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '标题',
  `desc` varchar(200) NOT NULL DEFAULT '' COMMENT '摘要',
  `tag` int(1) DEFAULT '0' COMMENT '是否分享到大厅 0，否，1，是',
  `author` varchar(20) NOT NULL COMMENT '作者',
  `source` varchar(20) NOT NULL DEFAULT '未知' COMMENT '来源',
  `cover` varchar(100) NOT NULL DEFAULT '' COMMENT '封面图',
  `content` longtext NOT NULL COMMENT '内容',
  `sort` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序（同级有效）',
  `view` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览量',
  `member_id` int(10) unsigned NOT NULL COMMENT '发布人ID',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `m_id` int(10) NOT NULL COMMENT '关联人员表',
  `share_time` int(10) DEFAULT NULL COMMENT '发表时间',
  PRIMARY KEY (`id`),
  KEY `member_id` (`member_id`),
  KEY `status` (`status`)
) ENGINE=MyISAM AUTO_INCREMENT=43 DEFAULT CHARSET=utf8;

/*Data for the table `rp_article` */

insert  into `rp_article`(`id`,`name`,`desc`,`tag`,`author`,`source`,`cover`,`content`,`sort`,`view`,`member_id`,`create_time`,`update_time`,`status`,`m_id`,`share_time`) values (1,'《淡写年华》','',1,'不知','网络','20171109\\ebc9fb0a64e1ddc3715cb848fc17d3e4.jpg','<p><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;许我们的心里藏有一个海洋，流出来的却是两行清泪，可我并不感到孤单，我只是简单地喜欢书写凄凉。曾经我们敷衍的情绪，在别人记忆的曲线里渐行渐远渐无言。难道曾经的悸动，只是岁月留给我一个人的错觉，我有点不相信自己的眼睛。我没有哭泣，只是学会了思念而已。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp;</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; 曾经我们也有过如海子一般“面朝大海，春暖花开”的美好愿景，曾经我们也为“那一年”的往事刻骨铭心，曾经我们也不知道忙碌夜夜狂欢，曾经我们也有过假装欺骗自己……在我们过去的时光里，一件件悲伤的、快乐的往事像一粒粒珍珠般串联在一起，构成了我们如花似玉的年华。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp;</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; 回首似水流年，内心莫名的宁静，也许是我们的心慢慢变老渐渐远离年轻。曾经，我们会为某件事伤心欲绝，也会为某件事欢心跳跃，我们会爱某个人爱到死去活来，我们也会恨某个人恨到不能自己，我们可以爱，我们可以恨，但我们不可以漫不经心。然而，年华像风里的白桦叶一般被风弄得沙沙作响，可我们现在的心平静得就像溪水般缓缓流淌，原来谁都可以不用顾忌着谁，原来谁都可以忘记离合悲欢，谁都可以简单地生活。</span></p>',0,3,1,1510218108,1510276088,1,1,1516679501),(2,'《不要轻易被身边的价值观所改变》','',1,'金木','网络','','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"text-indent: 2em;\">最近看了央视的一个节目《开讲啦》，开讲的嘉宾是清华大学副校长、著名科学家施一公。其中，他讲了这样一个观点——不要轻易被身边的价值观所改变。施一公讲了这样一件事：1987年，他还在清华大学读书，一天，他突然接到一个电报“父病危，速归速归”，当他心急火燎赶回家时，发现慈祥的父亲已经因车祸而离世了。他的天空一下垮塌了，以至于后来回到清华时，半夜溜到颐和园狂奔以求释放。大学毕业后，施一公想从政；后来，他又想到香港经商；再后来，他决定到外面的世界去看看，于是到美国去读博。博士毕业后，有著名的保险公司找他任中国首席代理，但施一公还是决定从事生命科学的研究。有美国大学聘请他担任终身教授，但他还是决定回国，他回到了母校清华。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">可以讲，对施一公而言，从政、经商、做保险、留美，凭他的智慧和能力，哪一个都能做得很好，甚至很极致。但施一公还是义无反顾的待在实验室，走上三尺讲台，在他看来，他要将生命科学的大事做到最好，将“大我”阐释到极致。施一公成功了，于是，他就有了一个极其重要的人生感悟——不要轻易被身边的价值观所改变。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">每一个人都有自己的价值观，社会上有形形色色的人，自然便有了形形色色的价值观。毋庸讳言，不少人的脑子里存在着“小我”的价值观，在工作的选择上，打着个人小算盘：工作环境要好、待遇要优、升职要快、社会地位要高。至于那些艰苦的工作、坐冷板凳的工作、待遇不高的工作，则很少去考虑。尤其是一些心浮气躁的年轻人，价值观很不稳定，容易被身边狭隘的价值观所影响、所左右，频频跳槽，如浮萍一般的飘忽不定。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">任何工作都需要有人去做，倘若人人都怕苦怕累嫌待遇低，那我们的国家还能可持续发展吗？还能实现中华民族的伟大复兴吗？中国，太需要有像施一公这样的具有“大我”价值观的高大上的人才。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">不要轻易被身边的价值观所改变，这是施一公的人生感悟，也应成为我们每一个人的人生信条，少些“小我”、多些“大我”，活出生命的精彩，创造人生的辉煌。</p>',0,1,1,1511400476,1511835777,1,1,1516679501),(3,'《时间赠人阅历，世事尽可原谅》','',1,'不知','网络','20171123\\9c6744fecab426df2fd807f928a0f64f.jpg','<p><span style=\"font-family: &quot;Microsoft Yahei&quot;; background-color: rgb(255, 255, 255);\">　心态，决定了很多事，愿我们能够保持一份好心态，生活的自在安然。繁杂人世中生存，难免遭遇烦扰，愿我们能够守住自己的一颗初心，从容而平静地度日。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　时间，是最霸道、最具说服力的。它无需向任何人知会，就把风雨磨难强加给我们。又自作主张地收起，抛给我们一天彩虹或阴云。但也教会我们认清一些无法辨明的人事，不再坚持和固执。宽宥人生，迷茫和努力的时间，考量了我们承受与隐忍的能力，生命，也因此变得厚重。这，就是“经历”的意义所在。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　世间任何一种事物，都有晦暗与光明、冷漠与热情、平淡与精彩的一面。人，亦是如此。处事，交友，看风景，都需要一份细心而坚持的了解。更要有所准备，接受其负面的一面，才不至于太过失望。慈悲而宽容地行走岁月，我们的心和目光种满美好和光明，才可以走出一条干净而长远的路。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　人总是要在经历一些事之后，才会领悟事实真相的无情。你曾以为的情深意重，也许根本就只是你以为；你曾以为的真诚善良，也许只是表象太美好。多少心机与生冷，藏在伪善的面具下，也许下一秒，便风驰电掣一般地变了。时间的缝隙里生长，领略了世事的无常与无情，心底的善良，会让我们原谅和宽容很多东西。学会自我保护、淡然处世的一刻，感谢那些伤害，让我们变得坚强；感谢那些不美好，让我们变得明智。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　人，来到这个世上，风雨飘摇、坎坷跋涉，悲欢离合、酸甜苦辣地一辈子，不知要承受多少苦难和煎熬；然而，我们却不能只看到风雨苦难，要想到雨后彩虹靓丽的天，要看到苦难过后岁月的宁静与安详。走错了路，别固执的不肯回头，你试着退一步，也许就是海阔天空的境遇。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　倘若走不近身边，请对你欣赏的人，保持起码的尊重和适当的距离。无论友情还是爱情，人心和爱，都是相对的。若不能暖透一颗心，就请转个身，面向阳光行走。相信终有一天，你会遇见更好更值得的。人生旅途，不必太多惊喜的奢望，只愿每一种缘分，都温暖、真挚便好。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　多年的梦愿无法实现，就安心享受现世拥有的一切；往事里的人，做不到了无牵挂，就心存感恩去怀念。原本简单的事，思虑太多，会让心负累；无法释怀的芥蒂，就留给时间慢慢消化。无论何时，心怀保持一份平和；熬不过、放不下的事，不要去为难自己；我们应该做的是：看淡一些，再看淡一些……</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　避开往事烟云的缠绕，精简岁月中的冗余，优化紧张而繁忙的生活，忘掉那些拉着世界一起下沉的日子。删繁就简的人生，记忆抛却那些不快乐，剩下的都是值得自己珍惜和感恩的人与事。情怀舒畅，心便会回归坦然宁静。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 18px; word-wrap: break-word; font-family: &quot;Microsoft Yahei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　时间，没有尽头，而生命终究有限。风云岁月中行走，若内心不缺少宽容与善良，必然会遇见理想的幸福人生！</p><p><br/></p>',0,2,1,1511405721,1511495535,1,1,1516679501),(31,'《浅遇深藏，温柔时光》','',1,'江水','网络','20171128\\38441e2c7086fdc1752a83cc9b9cd442.jpg','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">红尘冷暖，岁月清浅，仓促间，遗落一地，如诗的句点，不甘愿，不决绝，掬一份刻骨的思念，系一根心的挂牵，在你回眸的瞬间，一抹兰轩的底色，悄然纳于心间，不问深浅，暖一世的欢颜。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;----------<span style=\"font-family: 宋体;\">题记</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">&nbsp; 近来，有丝丝倦怠，铺天盖地的不肯离开，执笔，画圈，一闪而过的念，总是一而再，再而三的被搁浅，那些不情不愿的小情绪，占领了太多的空间，庸庸碌碌中，任光阴无情的在指缝中删删减减。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">&nbsp; 早春，微寒缠绵，风，温和的吹醒，春的语言，柳丝抽芽，百花争艳，桃花灼灼的世界是温婉多情的烟花三月。柳岸花堤，亭水榭间，行吟山水，一梦千年。“水光潋滟晴方好，山色空蒙雨亦奇。欲把西湖比西子，淡妆浓抹总相宜”。爱上一座城，爱它的雅韵别致，温润纤细，痴恋一场雨，恋它的清新淡雅，水墨成烟。空蒙蒙的烟雨，摇曳着柳枝，泪水涟涟。古往今来，江南春梦，被悠悠岁华，洗濯了千年。忧怨的姑娘，青石板的小巷，一柄多情的油纸伞，清晰而玲珑的在一湖瘦水中沉淀。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">&nbsp; 一朵时光，反复的被书写，写一程山水，春意翩翩，写柴米油盐，独自清闲，写万千的情意，羽化成美丽的语言，写小小的心绪，深深浅浅，心中，始终有一处温暖，如雨后的晨露，洋溢在眉眼，不增不减。佛说：“欲行净土，当净其心，随其心净，即佛土净。圣人求心不求佛，愚人求佛不求心；智者调心不调身，愚者调身不调心。”拥一颗禅心，平和宁静，不攀比，不争名夺利，在温和的光阴中，恪守自己，品人生百味，世事洞明。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">&nbsp; 我心中对于圆满，或许不是鲜衣怒马，也或许不是海棠佳话，而是，一段清简的况味，禅风流韵，与喜欢的人，素心相对，你护我冷暖，我慰你心安，在眼眸顾盼的瞬间，捕捉着岁月最美的箴言，一人一心，一花一叶。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">&nbsp; 如水的良辰，闲云悠悠，薄薄的阳光，还在云上，怀惴着晴朗，与风月对望，迎风中伫立，将繁华与落寞，细细丈量，佛说；万物皆有因果，世事变幻无常，命运，向你掩盖了一扇门，定会为你打开另一窗，只要你用心承载着阳光，便是明媚不可阻挡的方向。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 27px; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体;\">&nbsp; 行走在岁月的小巷，听风，读雨，夜色空寂，一切烟云，皆会慢慢散去，光阴眷顾，你我默默相依，慢慢老去，红尘若可安好，便可不悲不喜，不离不弃，情真意切，绵绵无期。</span></p>',0,1,1,1511838497,1511838497,1,1,1516679501),(32,'《夕照透入书房》','',1,'不知','网络','20180123\\280848c02d1858731d64d8f20950d2bf.jpg','<p style=\"margin-top: 0px; margin-bottom: 30px; padding: 0px; line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, &quot;microsoft yahei&quot;, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">怎样的事物才能真正地永存？阿房宫和华清池都已片瓦不留，李杜的名句和老庄的格言却一字不误地镌刻在每个华人的心里。世上延绵最久的还是非物质的——思想与精神。</p><p style=\"margin-top: 0px; margin-bottom: 30px; padding: 0px; line-height: 30px; font-family: &quot;Microsoft Yahei&quot;, &quot;microsoft yahei&quot;, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　昨天的哲人只能解释昨天，今天的答案还得来自今人</p>',0,8,34,1516669083,1516669083,1,34,1516679501),(34,'《半暖时光里青春的颜色》','',0,':那安','网络','20180123\\f4e5de0457854b4c5adc73b135cb0357.jpg','<p><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp;我的时光里，一半是温暖的，一半是冰凉的。我的青春中，一半是暖色调，一半是冷色调。这就是我在半暖时光里青春的颜色，冷暖交错着，没有明确的界线，只有模糊的心情。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp;</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; 在那一半温暖的时光里，父母和朋友给我的青春添上了种种暖色，陪伴着我走过那段青春之旅。父亲支撑起整个家，给予我满满的安全感；母亲细心地照顾我，给予我全心的呵护；朋友一路相随，给予我深厚的情谊。他们有着各自的色彩，有暖色也有冷色，却总是尽最大的努力把暖色给我，尽量不让我看到冷色。可以说，他们为我建立了一个安全的堡垒，我的青春有一半是在他们的保护和关心之下度过的。</span></p><p><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\"><br/></span></p><p><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">在那一半冰凉的时光里，爱情是我青春里的最复杂的颜色。一开始，爱情是甜蜜的粉红色，每天我都像是徜徉在蜜糖里似的，乐不思蜀，以为这就是爱情全部的模样，会一直这么甜蜜幸福。可是后来，爱情沾染上忧郁的蓝色，染到了沉默的灰色，还有其他让我心寒的颜色。最后这份爱情只能在记忆中寻找到最初的粉红，一种种冰冷的颜色如同泼墨一般洒在了爱情粉红之上，封锁在记忆的深处，害怕一想起就是整片整片的灰暗和寒冷。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 半暖时光里青春的颜色时暖时冷，映衬着最初的天真纯白，就像在一张白纸上添加各种颜色，形成一幅色彩斑斓的画，记录着我在青春里遇见的每一个人，得到的幸福，失去的悲伤，丰富着我的人生。</span></p>',0,0,34,1516685807,1516685807,1,34,NULL),(35,'青春不倾城','',1,'米七七','网络','20180123\\57832ea3ed41df37896e4f9a072475be.jpg','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">&nbsp;青春是我们最华丽的季节，却怎么也伤不起，这是貌似无懈可击的人生，我们自以为是的规划好了青春，可终究抵不过成长过程中的纷纷扰扰，我们多少次陷入被长大的误区，多少次迷茫在成长的轨道之外，多少次和我们的朋友们渐行渐远，会有那个少年仍陪你成长吗？</span></span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">　　我们的青春慢慢的老去，可我们却一如既往年轻着。我们向往的是洒脱的不羁，却怎么也实现不了那种嚣张的自由；怕一次次的丢了我们的青春再也找不到年少时的单纯；每天面多形形色色的人和事，每天都假惺惺面对自己面对别人，做着假惺惺的梦，感受着假惺惺的情绪和感动，会有那个少年给你温存吗？</span></span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">　　不要嘲笑我的荒唐，我也有值得坚守的梦想，期待我长大后的模样，是否还会让你们笑的猖狂！当所有人都以为我们会亲手用浮夸，狂妄，自大埋葬了自己的青春时，当我们费尽心思去辉煌我们的人生时，当我们一个人蹲在墙角擦拭被岁月的刻刀划得斑驳的伤痕时，会有那个少年或者少女蹲下来给你一个拥抱吗？</span></span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">不需要谁去惊艳时光，只希望你能在需要的时候温柔我的岁月，不渴望你轻易许下的一辈子，只希望知道现在你还在。即使世界上没有你，忘川水同样很凉，彼岸花同样会开。</span></span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">　　你有你的选择，我有我的决定，你有你的理想，我有我的志向，我们往往会殊途却不同归，可世界上哪有那么多的永远，一辈子可能只是笑话。</span></span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">　　不论怎样，不去过多的思考未来，现在总要心念着彼此，总要在彼此心里刻下痕迹。岁月是把杀猪刀，淡了情意，伤了流年，薄了思念，凉了你我。时光荏苒，岁月蹉跎，即使在珍惜彼此，我们总要长大，总要看淡遇见，看透离别。时光要我们长大，没时间去过多的心伤，去怀念。我们总要成熟，总该承受，总会习惯。</span></span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-size: 11px;\">　　总有一天，我们会放下一切，将我们的哭泣也夹在书页里，好像我们年少时的的那几朵茉莉花，也许，会在多年后的一个黄昏里，从偶尔翻开的输的扉页中悄悄滑下，没有芳香，再无声息，再不留恋。<br/><br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 白衣苍狗，世事无常，思忆过去，念及未来，青葱岁月，光辉年华，微笑面对，好好生活。</span></span></p>',0,3,34,1516685989,1516686007,1,34,1516687012),(36,'《与你相约，是一种禅意》','',1,'微尘陌上','网络','20180123\\944a6c78daa105a9132509978d0cb78c.jpg','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">与你相约，是一种清浅的禅意，而我，便在红尘最深处的禅意里，等你--题辞.微尘陌上<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　窗外，细雨的绵柔音律，偶尔听听，是清宁的，洒落在我的庭院，也肥了那株茶树的花枝。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　想来，流年暗换，三月已不是那段枯瘦的光阴。旧年的那首《葬花吟》，只剩短歌余韵，如一阕殇词，清浅地沉落，忧伤而不失风雅。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　与你相约，等待一场即将到来的花期，于我，是一个小小的欢喜，因为，我偶尔地想你，是一种忧伤的美丽！<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　春雨，滴落的不仅是水滴，还有旧年那一朵茶花的香韵；雨里，放眼循着美人香草的小径，寻一朵花魂的归期。或许，归期是遥遥无期，而去日却是皁经远去，给人留下或多或少感伤的憾意。是呵，遥望那些旧年里走散的日月，情感总在流放中，流放成一种固执，在每一场花落之后的日子里，等待再一次花开的声音。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　如果，可以用一生的打坐，笃定成一种禅意，那么，我深信，一直等下去，等待一个人一场天荒地老的爱情。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　与你相约，将芳华正好的青春，等待成一个暮年苍苍的老人。晨钟与暮鼓，小楼与长亭，红颜与风情，在写给你的文字里，字字余香，会成为那些岁月里永恒的风景，让我记得。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　美人香草，浅窗兰叶，茶花香魂，蓝鸟声声，和着三月的雨声绵柔，是否，你能听见，我的心语依旧一如花开的声音。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　与你相约，茶花开时，人已来。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">是谁，在一纸浅墨文字里安静等待？是谁，在一路时光里仆仆风尘？又是谁，在一叶轻舟里只影远行？<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　那个人，是清浅岁月里最沉重的那一滴雨，在记忆的天空，季季滴落，季季来去。我流连在三月的雨季，看见永恒的天空已然由暗灰变作了烟青，看见蛮荒的塬上渐渐滋生出生命嫩绿的芽子，写意成一帘梵净山里早春的风景。一树茶花，朦胧烟雨，借着三月乍暖还寒的春光，我分明看见，晶莹的泪珠两行，悄然跌落，是滑落过岁月绸帕上的清冽水迹。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　很多时候，我想，你一如深山里的山茶，你一如幽潭里的素莲，在未至的花期里，幽隐着，而我，便在与你相约的时光里，对生活心怀敬意，--期待，在那一个花期来临的日子，一睹你花开的样子。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　如此，我退居于山野小村，营生着一颗素朴的心，在不计时长的小村时光里，热爱着平凡的事情，热爱陌生人家的爽朗笑声，热爱市井人生的无聊嬉戏，也热爱小猫小狗在日头下的慵懒困意，偶尔也吃茶养花，偶尔也读书听雨，沉湎于日月星辰、花草田间、露水晨昏，在低温的日子，不急不缓，深情地活着！<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　与你，相约，是一种清浅的禅意，因为，想你，是一种忧伤的美丽！<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　初春的雨，总是会落进心里的某个地方，或者落进某一个不经意间涌动的念里，天青的颜色，乍暖还寒时候，最难将息。就如看见细雨飘零，总会令人无端想起旧年的黄梅子黄梅雨，还有一个遥远北方的你。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　来于大千，行于世间，或许，每一个人都会有极致的孤独，每一个人都会有深眷的时刻，都有一个可以唯一与之相约的人，那个人，或许在天边，或许在近前，而那一款独一的深情，却是极致到不悔，就算，真正能陪着自己走完人生的，只有时间最钟情。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　世界大无穷，可融芸芸众生，你与我，恰恰都是众生里的两个，就如两枚山茶花，原本该是各自幽香，不近风尘，不染亲疏，但这世界偏是又小如犄墙，在墙影的转角处，那个彷如山茶花开的你，非得让我遇见，从此，有了牵连，与你有关，让我活着的深情，细腻而丰盈。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　你是一个怎样的女子呀？在即将来临的花期，会像一朵艳丽的山茶，繁丽的盛开在我写给你的那一纸文字幽角，在三月的时光里，繁荣我的那些没有藩篱遮拦的往事茶园，在第一朵茶花绽开的时候，与你温上一壶梵净山新出的绿茶，游历在武陵正源海拔高度1000米处的茶树茶花茶叶的每一寸清幽世界，惬意时弹笑，寄情江湖，悲凉时潸泪，快意人生，一如季节更替，枯荣自觉。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　我想念你，在春茶采收的季节，是如此深情的；我想念你，在那一朵茶花飘香的日子，是如此忧伤的；我想念你，在梵净山悠悠的梵唱里，是如此清浅的。<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　与你相约，是一种清浅的禅意，而我，便在红尘最深处的禅意里，等你！<br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　时间如来，不期而至，世界如是，相见恨晚，若能守得一隅清明，只许你与我共度，做梵净山里的飞鸟相与还，朝花夕拾，煮茶焚香，杖藜执酒，白马轻舟，君可愿？</p><p><br/></p>',0,2,34,1516686162,1516686162,1,34,1516687008),(37,'《月夜美》','',1,'艺文','网络','20180123\\f02497fb8835bec6b5e6fed4fc5c11ef.jpg','<p>日夜思念的明月映入眼帘</p><p>慌忙的寻找黑夜的一边</p><p>月光浸透了大地的脸</p><p>月光下的大地羞羞的笑了</p><p>秋冬的月夜悲凉残愁</p><p>萧瑟的风吹打着你的身体</p><p>充满怨气的树叶阻碍你的旅途</p><p>这样的月夜充满寂静</p><p>充满伤悲，充满思乡人的眼泪</p><p>有点失落，月色残破</p><p>泛出黑黑的，金黄的脸</p><p>月夜愁淡着脸，在没有人凝记的一天</p><p>消磨了属于自己生命的一段</p><p>凝视着今晚的月亮，心中交集百感</p><p>钟爱的人不多，月夜风景又如何</p><p>借着茫茫的月色抒发伤感</p><p>月夜美，愚人不知又如何</p><p><br/></p>',0,4,34,1516686299,1516686299,1,34,1516687003),(38,'《雪》','',1,'子非鱼0','原创','20180123\\fa6acbbf7be48c091dd0c08205c59b68.jpg','<p><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">天地间有种东西叫做雪，从天而降，落地而化；人世间有种东西叫做爱，吸引中诞生，升华中融合。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;上述这句话出自一位故友的语录，出了此言，他的女孩说道：“我也喜欢你，我答应做你的女朋友。”</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;什么是雪，雪便是纯洁。这来自天国的陌路者飘逝处，我们看到了大真、大善、大美。它的前身是水，我爱那水，不仅因为它的纯美，更在于它的洞府深处潜藏着的灵性。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;水，无形，无态；水亦可以有形、任意态。这便是灵动的水，而雪继承了这种灵性。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">-</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;天地间有东西叫做雪，散天漫舞，遁地无声；人世间有种东西叫做爱，相知时散飘，相恋时飞仙。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;上述这句话出自我，出了此言，我依旧静静的等待，在路口久久守望。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;什么是雪，雪便是虚无。这暗淡于地府的无名地精处，我们觅见了大喜、大悲、大空。它的前身是光，我爱那光，不仅因为它的明亮，驱散了黑暗，更在于它的心灵深处蕴含的的力量。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;光，无影，无踪；光亦可以寻踪觅影,击碎所有的大悲、大喜，转化成为大空。这便是光，来去无踪，而雪继承了这种力量。</span></p><p><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">这种力量的美与这种灵性的真，并非今天空间序的主题。下面是一句作为自由身后送与友人的感触——</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;朋友中有一个人是你，识于偶然，止于永久！愿蓝天为纸，清风为笔，祝福为星，点上真诚星星，描上白云，奉上月亮，晓看今宵明晨，绘上阡头，题上陌柳，相约永远永远。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">-</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;这个序题得不太正规了点，大抵不扰清闲中的碌碌，也算过得去。合上这杂序，有一种思念飘飞。</span><br style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;今夜有星，愿眠各位友人。</span></p>',0,2,32,1516686465,1516686465,1,32,1516686470),(39,'《我们还在一起会是怎样？》','',1,'海棠','网络','20180123\\b4d8e94c335b85c7592524f7de577088.jpg','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">如果，我们现在还在一起会是怎样，我们是不是还依然深爱着对方，像开始时那样……</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">听着自己的心跳，没有规则的跳跃，安静的在思考，我们曾紧紧拥抱最后却又轻易的放掉，你明明也难过还安慰我要好好生活。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">最后转身的瞬间像又回到了开始时那样，重新触动不舍的对方，如果你迟一些放开手，我早一些回头，是不是现在也重新在一起？</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">那年夏天的光影，短暂的只有一首歌曲的时间，生活的紧迫感，随时充斥着我们的心灵，一天如一天的浑水摸鱼般的浑噩度过，细水缓缓地深流。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">岁月不断地蹉跎着，只是脸上的斑点日益增多，还有写不完的歌曲，梦不完的记忆，回不到的过去。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">多少次的期望，多少次的回头，慢慢的变得朦胧暗淡，茫然眼前的一切，连悲伤都找不到借口。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">如今想想，曾经的失去是那么的珍贵，总感觉时间过得太快了，仿佛一切还在昨天。极目眺望，有你的方向，温柔的将所有的想念都悉数凝聚在心上。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">很多时候都在想，人与人之间的缘份会怎样，一路走来，一路丈量，一份情意，此生不忘，时间的力量，强大到不可原谅，曾经年少的脸庞，被岁月烙上了沧桑印象。</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">时间分割成对角，瓦解我们的依靠，停止你对我的好，那种感觉很微妙，要怎么说才好。</p><p><br/></p>',0,3,32,1516686680,1516686680,1,32,1516686693),(40,'念，是种味道','',1,'维纳斯','网络','20180123\\6e5fd52b4c75b44f370e51177a80d114.jpeg','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">关关雎鸠，在河之洲。窈窕淑女，君子好逑。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;求之不得，寤寐思服。悠哉悠哉，辗转反侧。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这首《诗经》里面的作品《关雎》，可谓是人人皆知。它生动刻画了一位男子仰慕着那在河之洲的心上人。而到底是怎样的“思慕”之情？正处于情窦初开的我们乍拜读这首诗歌，或许不能完全体会到。直到后来我遇到了她，才彻底领悟到了其中的奥秘所在</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">从相识到相知相爱，我们在这段时间里，彼此爱的越来越深。我们在一起的时候，每天都能见上一面。然而，距离让我们只能通过现代科技保持着那份感情。所有的聊天、语音、视频，都感觉那么虚幻，飘渺。没有和她在一起时那样安心、舒适、真切。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每天清晨，当晨曦的第一抹骄阳唤醒我臃肿沉睡的眼睛，在大脑开始有规律地运转时。我便不自觉地想起来了你，打开你的聊天界面，打上冷冰冰的几个字：“早上好，亲爱的”。不不不，那不是我想要的。我希望每天早上可以赖在你的耳边轻声温柔地唤醒你，对你说上一句早安。距离让我只能通过冷冰冰的方块字去传递我热烈的内心情感。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;每天夜幕降临，迎着晚风，望着满天星辰。我把你比作天上的那轮月亮，皎洁的月光洒在我的每一寸肌肤上。“冰清玉洁”或许是对你的美最好的诠释。夜色渐渐地深了起来，我躺在伸手不见五指的黑暗中，脑海里关于你的记忆片段便犹如火山爆发一般，喷薄而出，思念之绪便油然而生。我伸出手来，想去抓住眼前的你。虽然知道那是幻觉，可仍想去试试。我此时理解了“辗转反侧”的含义。心里念着一位佳人的苦楚是用语言无法形容的，我躺在冰冷刺骨的床上，想象着你在我身边的情景。不知此时的你，被子盖好没？不知此时的你，冷不冷？我什么都不知道……，还是那几个冰冷的方块字：“晚安，宝贝。”我受够了，这些字刺痛了我的双眼。多想睡前能亲吻一下你的额头，然后甜蜜地对你说一声：“晚安，亲爱的，好梦”。我的感情怎能凭借区区这几个冰冷的文字就能完全地表达出来。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;&nbsp;我知道你爱吃的食物，所以好想一直陪在你的身边，每天都烧给你吃。我知道你胃偶尔会不舒服，所以特别担心你的身体。无论科技再怎么发达，这些事情是它所不能办到的。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;“衣带渐宽终不悔，为伊消得人憔悴。”，是我对你深沉的眷恋。“琴瑟友之”、“钟鼓乐之”，是我的精神慰藉。距离，让我更好的体会到了思念的味道。很苦，真的很苦。这暂时的分开，让我更加期待珍惜和你下一次的相见。</span></p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;&nbsp;&nbsp;念，有种不可言喻的苦楚。思，有种身处水深火热的焦灼等待。唯有陪伴，才是化解这种矛盾情感的良药。愿以后的我们不再分离，长相厮守。</span></p><p><br/></p>',0,1,32,1516686904,1516686904,1,32,1516686915),(41,'陪我，好吗？','',1,'维纳斯','网络','20180123\\924d103c3500798a0bea17c225da2a40.jpg','<p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">这或许是立春以来的第一场雪吧！在我脑海中模糊地记着。从昨晚开始，天空中便稀稀落落地落下几片雪花。紧接着，雪愈下愈大。&nbsp;独自守着窗儿，百无聊赖地看着路边一盏盏撒着淡淡余光的路灯，独自欣赏着灯光下正在激情舞着的冰清玉洁的雪精灵。它们一个接一个地拍打在我的窗上，好似在邀请我去随它们跳上一支舞。风儿也在疯狂地呼喊着我的名字，迫切地来让我参加它们的聚会。我努力打开窗户，任冷风吹打着我的肌肤，从头到脚，从表层到心里。惊奇的是，我没有感觉到一丝丝冷意，而是满满的暖。一股股热流自内而外喷薄而出。我的灵魂摆脱这早已桎梏的躯体，前来加入这场宴会。</span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;风儿奏乐，雪花尽情地舞着。我随着它们的节奏慢慢地跳起来，越跳越尽兴。不知不觉，双眼早已湿润，晶莹的泪珠尽情地洒落在这空旷的地面上。往事，像针扎一样刺痛着我的神经，我努力回忆着关于你的零星记忆。这路灯下，不是我们曾经相见的地方吗？这片空旷的土地，不知留下了我们的多少只脚印。今晚，路灯还在，那一株小草还在，风还在，雪还在。我还在，而此刻的你去哪了？真得好想念你。我问路灯，问雪、问小草、问风，它们也不知道。我问我自己，更是茫然不知。我只好让雪带去我的思念，顺着风儿飘向你回家必经的路上，落在你的窗台。不知此刻的你是否已经安然入睡，不知你看到了那片雪花没？</span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp; &nbsp;我对你的爱，没有保质期。每天永远保持第一次爱你时的新鲜感。我对你的爱，日月可鉴。虽海枯石烂，坚贞不渝。曾经好多次问别人，爱情是什么味道的。他们众说纷纭，以至于我对“爱情”越来越模糊，越来越抽象。直到遇见你，我才明白了爱的真谛。爱情，有种想保护你的冲动，不愿让你受半点委屈。爱情，就像蜂蜜一样甜，甜到了我的心里。和你在一起的时候，每一分，每一秒。都变得如此珍贵，都变得如此有意义。而，爱情。它有时又是痛苦的。当和你分开时，念你的思绪便油然而生。记忆真像一把利剑，让此刻的空气都变得沉重，让此刻的时间都变得如此难熬。</span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp; &nbsp;你陪我度过七年之痒，可好？我想以此给你一辈的幸福。你用青春下注，我陪你白首。可好？你用青丝、红唇为染料，我为你书写一幅宏伟壮丽的蓝图。可好？在我沮丧的时候，你给我一个肯定的微笑。而当我们年老时，我搀扶着你，一生一世陪在你身边。我想，我是真心爱你的。我想，我早已中了上天赐给我的“这杯毒酒”。虽还有心跳，虽还呼吸着。可没有你的时刻，我便如行尸走肉一般，失去了灵魂。</span><br/>&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 1em; padding: 0px; list-style: none; text-indent: 2em; color: rgb(79, 93, 65); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;&nbsp;能不能，留下来，陪着我？时时刻刻，我想这或许很自私。但这是我内心真实的想法。我已经没有任何语言来表达我对你的爱了。</span></p><p><br/></p>',0,5,1,1516687121,1516687121,1,1,1516687126),(42,'叶与凡花','',1,'The gentry','网络','20180123\\72f0749fb8b2c60431f0bfd2d4ec9fed.jpeg','<p>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;叶与凡花 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 雨鹰 &nbsp;</p><p>&nbsp;</p><p>&nbsp; 1961年，一位来自新西兰的画家偷渡到了法国。两个月后，他来到了法兰克福。</p><p>&nbsp;</p><p>&nbsp; 这里金融业繁荣，各种大腕出没于银行与股票交易所之间。这位画家在街道上低头行走，他感到自己的身份卑微，别人一只手上戴的饰品就足够买下一座别墅。他却只有一支画笔和一副画板，还有那空空如也的钱包。</p><p>&nbsp;</p><p>&nbsp; 然而，一个普通的人也怀有梦想。并不富有的他为自己的画作感到自豪。他住在一个小胡同里，出行不便，屋檐交接处遮挡了大部分的阳光。他每天都会拿起自己的画笔，轻轻描绘着所见的一切。每当他将一副作品卖与他人，他都会严格遵守自己的原则——穷人只收百分之50而富人则加倍收钱。当初的他因此生活困窘，加上结交的朋友多为底层人士，因而最后他想到了离开。</p><p>&nbsp;</p><p>&nbsp; 一个早晨，他来到了火车站，在他准备买票时，他见到了一位女孩。一位身着金装，却微笑着用钱包里的现金施舍着站角的乞丐的女孩。她目光温润，似美玉；她轻抬足尖，似公主；她面浮微笑，似仙女。他细细欣赏着这位女孩的一举一动，心中万分美妙，如含饴细尝。路人见到他站着不买票，问道：“你是在看她吗？”他迟言道：“是的，她真的很美。”“怎么，爱上她了？”他慢言道“我想是的。她在施舍穷人时，我仿佛看到了一束繁花盛开在绿叶之间。阳光莹溶在她的花朵中，露珠伏倒在她的花香里。”路人讥笑着说：“我劝你还是早日收了这份心吧。她可是罗氏的长女，弗兰小姐。她的家族地位显赫，甚至连当年的法国王室都要对他们言听计从。”画家笑着回答：“如果没有叶的衬托与依持，恐怕再美的花也会在一夕间凋落。”语罢，画家离开了火车站，他下定了决心要追求那个女孩。他深深沉浸在之前自己所幻想的画面里。繁花在绿叶中盛开，似云中明月，傲生地间。</p><p>&nbsp;</p><p>&nbsp; 第二天，他开始创作，没有实物为照，空凭想象。六天后，他完成了自己的新作。这幅画画的是一折枝节，枝节上绿叶交错，而在其间，盛开着美艳的花朵。他因此将它取名为《叶与繁花》 。他高举画作，前往罗氏庄园。一路上不时有人惊呼：“如此美丽的画，我愿意出200法郎。卖给我吧。”甚至一位商人说愿意出2000法郎的高价，但这些在他的眼里，都不足以与弗兰持平。他坚定地来到罗氏庄园的门口，却被人拦住了去路。“穷小子，这里可不是你该来的地方。快回去吧。”看门人带有蔑视地说。被人驱赶的他，依旧坚持着自己的决心。他说：“我可以离开，但请你们帮我一个忙。请帮我将这幅话交给你们的弗兰小姐，就说是我献给她的。感激不尽。”管家接过画，一番欣赏后连连叫绝，就勉强同意了。见到管家答应后画家连声致谢，笑着回去了。</p><p>&nbsp;</p><p>&nbsp; 一天后，他收到了一份包裹，内有一幅画和一份信。当他梦幻着会看到弗兰的赞语时不料却看到了以下的字：</p><p>&nbsp;</p><p>&nbsp; 谢谢你的画。你的画的确很美。起初我以为你技艺非凡，可是当我看到你后面的寄语时，我就不在喜欢你的画了。你只是觉得我很漂亮，可我要找的男人不是那样的。我不在乎他贫富与否，也不在乎他郎才篇篇，我只希望有一个能够真正懂得我内心的人。或许你还差那么一点，请别在打扰我了。</p><p>&nbsp;</p><p>他哭了，眼泪滴落在信纸上，透过湿润的纸，他看到了自己微颤的手。他悲愤难耐，经折断了画笔，踢翻了画板。他抱怨着自己的无能与无知。“伟大的主啊！请原谅我的无知，一个穷小子有什么资格追求富家千金呢。我手中的画笔，注定描绘着我的悲剧人生，或许当我步入荒年，它也无法作出彩虹。”他靠在门上，用额头猛撞，当额头泛起血色，他又仰面倒在地上。“伟大的主啊！为何一为真情的人不能欣赏自己喜爱的花朵呢，他没有家财万贯，可他秉持着一颗赤诚的心。”那晚，他于地而眠，待他醒来，已是日暮黄昏。</p><p>&nbsp;</p><p>&nbsp; 泪痕依旧遍布在他苍白的脸上，久久不去。</p><p>&nbsp;</p><p>&nbsp; 当他回头看见破损的画板和折断的画笔时。他自责不已。自己的挚爱竟然被人损害的如此严重，是哪个穷凶极恶之徒？他恍然想起，竟是自己。“我怎么成了这样？一个画家最重要的天赋是善于平静的心，可我为何亲手打破了它？我有梦想，怎么放弃了呢。”此时此刻，他似乎醒悟了，那个女孩就是他的梦想——迎娶她。</p><p>&nbsp;</p><p>&nbsp; 自此，他每日握笔作画，却一直没有好的灵感。一个雨天，他望向窗外，当他看到雨点在叶间跳动时，他的心被那身着透明雨袍的白兰所吸引。一时间他不知该说什么，万分狂喜。他想这就是最好的灵感——一朵平凡的白兰在绿叶中盛开。想到这里，他细细琢磨。开始作画的他，一直面带微笑，画笔轻轻舞动，线条优美。十天后，他完成了这幅画。这幅画里只有一朵白兰和几片绿叶，却显得极为和谐。他为它取名为《叶与凡花》 。</p><p>&nbsp;</p><p>&nbsp; 他终于送出了自己的第二幅画，可是过了一周都没有收到任何来信。他早已猜到会有这个结果，如今放下心的他真的准备离开了。可就在第二天早晨，有人轻敲他的门。他疑虑着是谁，慢慢打开了门，来人竟然是弗兰。弗兰微笑着，深拥住了他。画家紧紧抱住了她，眼泪慢慢湿润了眼角。弗兰说：“你的画真美，你的朴实与诚意打动了我，你就是我要找的男人！”画家如闻佳曲，心喜久驻。这天起，他们开始了正式的交往。同时，画家将过去收藏的自己的画作一份份卖出去，由于受到罗氏名誉的影响，原本几百法郎的画暴涨了十倍。人们都认为他就是罗氏的新人驸马。还没有来得及参与罗氏的财产分割的他，就已经因为售画有了不少积蓄。</p><p>&nbsp;</p><p>&nbsp; 半年后，他们成了婚。当年那位路人发现他口中的穷小子如今迎娶了弗兰小姐时，嘴巴惊张到快要脱臼了。这似乎不可能的事竟然在他身上得以实现了。</p><p>&nbsp;</p><p>&nbsp; 画家一直珍藏着两幅画——《叶与繁花》和《叶与凡花》 。</p><p>&nbsp;</p><p>&nbsp; 他在他的《叶与凡花》的寄语中写到：这幅画不及上一幅的精致，可它暗藏了一份礼盒，这个礼盒的名字叫真诚。如果你有意收下它，请先不要打开它，因为一旦你打开了它，你会发现它里面还有一个礼盒。这个礼盒没有名字，因为它由爱所制。如若你想打开它，请你也用你的爱来制作一个礼盒。当我们收下他们时，将会为彼此打开。爱的寄语不必精饰，因为我对你的爱纯如清水。</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 作者寄语：谨把此文献于那些追求梦想与真爱的人。</p><p><br/></p>',0,18,1,1516687203,1516687203,1,1,1516687209);

/*Table structure for table `rp_asset` */

DROP TABLE IF EXISTS `rp_asset`;

CREATE TABLE `rp_asset` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `user_id` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '用户id',
  `file_size` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '文件大小,单位B',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上传时间',
  `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态;1:可用,0:不可用',
  `download_times` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '下载次数',
  `file_key` varchar(64) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '文件惟一码',
  `filename` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '文件名',
  `file_path` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '文件路径,相对于upload目录,可以为url',
  `file_md5` varchar(32) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '文件md5值',
  `file_sha1` varchar(40) CHARACTER SET utf8 NOT NULL DEFAULT '',
  `suffix` varchar(10) NOT NULL DEFAULT '' COMMENT '文件后缀名,不包括点',
  `more` text COMMENT '其它详细信息,JSON格式',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=99 DEFAULT CHARSET=utf8mb4 COMMENT='资源表';

/*Data for the table `rp_asset` */

/*Table structure for table `rp_category` */

DROP TABLE IF EXISTS `rp_category`;

CREATE TABLE `rp_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '标题',
  `pid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上级分类ID',
  `level` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '深度',
  `type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '类型 0内部栏目 1单网页 2外部链接',
  `model_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '模型ID',
  `url` char(100) NOT NULL DEFAULT '' COMMENT '链接地址',
  `sort` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序（同级有效）',
  `is_nav` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否在导航显示',
  `hide` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否隐藏',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  PRIMARY KEY (`id`),
  KEY `pid` (`pid`),
  KEY `status` (`status`)
) ENGINE=MyISAM AUTO_INCREMENT=121 DEFAULT CHARSET=utf8;

/*Data for the table `rp_category` */

/*Table structure for table `rp_config` */

DROP TABLE IF EXISTS `rp_config`;

CREATE TABLE `rp_config` (
  `option_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `option_name` varchar(64) NOT NULL COMMENT '配置名',
  `option_value` longtext NOT NULL COMMENT '配置值',
  `autoload` int(2) NOT NULL DEFAULT '1' COMMENT '是否自动加载',
  `option_lang` varchar(10) NOT NULL DEFAULT 'zh-cn',
  PRIMARY KEY (`option_id`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='全站配置表';

/*Data for the table `rp_config` */

insert  into `rp_config`(`option_id`,`option_name`,`option_value`,`autoload`,`option_lang`) values (1,'site_options','{\"site_name\":\"\\u4efb\\u9e4f\",\"site_keywords\":\"\\u9633\\u5149  \\u5e05\\u6c14  \\u6e29\\u6696\",\"site_desc\":\"\\u4f18\\u79c0\\u9700\\u8981\\u517b\\u6210\\u3002\",\"site_phone\":\"18382106376\",\"site_QQ\":\"513494629\",\"site_copy\":\"\\u4efb\\u9e4f\",\"site_login_bg\":\"bg.jpg\",\"site_beian\":\"\\u4efb\\u9e4f\\u4e2a\\u4eba\\u7f51\\u7ad9\"}',1,'zh-cn');

/*Table structure for table `rp_link` */

DROP TABLE IF EXISTS `rp_link`;

CREATE TABLE `rp_link` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `url` varchar(100) NOT NULL,
  `create_time` int(10) NOT NULL,
  `update_time` int(10) NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '1',
  `m_id` int(10) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

/*Data for the table `rp_link` */

insert  into `rp_link`(`id`,`title`,`url`,`create_time`,`update_time`,`status`,`m_id`) values (1,'个人博客地址','http://www.cnblogs.com/RPmorning/',1510215625,1510215625,1,1),(2,'个人Github地址','https://github.com/RPmorning',1510215647,1510215647,1,1),(3,'烧IT','http://www.shaoit.com/',1510215670,1510215670,1,1);

/*Table structure for table `rp_logs` */

DROP TABLE IF EXISTS `rp_logs`;

CREATE TABLE `rp_logs` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `member_id` int(11) NOT NULL COMMENT '操作人员id',
  `member_ip` char(15) NOT NULL COMMENT '操作员ip',
  `behavior` varchar(255) NOT NULL COMMENT '行为',
  `model_name` varchar(255) NOT NULL COMMENT '模型名称',
